mapper.select:
SELECT MKMM_WORK_FLOW.ID AS WORKF_LOW_ID, MKMM_WORK_FLOW.REVIEW_LEVEL AS REVIEW_LEVEL, MKMM_FEE_MAIN_REVIEW.REVIEW_STATUS AS REVIEW_STATUS, MKMM_FEE_MAIN_REVIEW.ID AS ID, MKMM_FEE_MAIN_REVIEW.BUSINESS_CODE AS BUSINESS_CODE, MKMM_FEE_MAIN_REVIEW.BUSINESS_NAME AS BUSINESS_NAME, MKMM_FEE_MAIN_REVIEW.COMPANY AS COMPANY, MKMM_FEE_MAIN_REVIEW.FLOW_ID AS FLOW_ID, MKMM_FEE_MAIN_REVIEW.CREATE_TIME AS CREATE_TIME, MKMM_FEE_MAIN_REVIEW.CREATOR_NAME AS CREATOR_NAME, MKMM_FEE_MAIN_REVIEW.MODIFIER AS MODIFIER, MKMM_FEE_MAIN_REVIEW.FLAG as FLAG FROM MKMM_WORK_FLOW , (SELECT REVIEW_ID,MAX(ID) AS MAX_ID FROM MKMM_WORK_FLOW GROUP BY REVIEW_ID ) MAX_WORK_FLOW , MKMM_FEE_MAIN_REVIEW WHERE MKMM_WORK_FLOW.REVIEW_ID =MAX_WORK_FLOW.REVIEW_ID AND MKMM_WORK_FLOW.ID =MAX_WORK_FLOW.MAX_ID AND MKMM_WORK_FLOW.REVIEW_ID =MKMM_FEE_MAIN_REVIEW.BUSINESS_CODE AND MKMM_FEE_MAIN_REVIEW.FLAG ='1' AND MKMM_WORK_FLOW.REVIEW_STATUS in('02','03')
AND MKMM_FEE_MAIN_REVIEW.BUSINESS_CODE LIKE CONCAT(#{businessCode},'%')
AND MKMM_FEE_MAIN_REVIEW.BUSINESS_NAME LIKE CONCAT(#{businessName},'%')
AND MKMM_WORK_FLOW.REVIEW_LEVEL =#{reviewLevel}
and MKMM_FEE_MAIN_REVIEW.REVIEW_STATUS = #{reviewStatus}
AND MKMM_FEE_MAIN_REVIEW.REVIEW_TYPE = #{reviewType}
AND MKMM_FEE_MAIN_REVIEW.COMPANY = #{company,jdbcType=VARCHAR}
AND MKMM_FEE_MAIN_REVIEW.AREA_CODE = #{areaCode,jdbcType=VARCHAR}
AND MKMM_FEE_MAIN_REVIEW.FOUNDER_LEVEL in ('02','03') AND MKMM_FEE_MAIN_REVIEW.FOUNDER_LEVEL in ('01')

select a.BUSINESS_CODE as BUSINESS_CODE, a.BUSINESS_NAME as BUSINESS_NAME, b.AREA_NAME as AREA_NAME, b.BRANDS_NAME as BRANDS_NAME, a.REVIEW_TYPE as REVIEW_TYPE, a.FLOW_ID as FLOW_ID, a.REVIEW_STATUS as REVIEW_STATUS, a.Match_ID as Match_ID, a.BILL_STATUS as BILL_STATUS, a.IS_SEND_GX as IS_SEND_GX from MKMM_FEE_MAIN_REVIEW a, MKMM_ACTIVE_MATCH_MAIN b where a.Match_ID = b.ID and a.REVIEW_STATUS in ('00','03')
AND a.BUSINESS_CODE LIKE CONCAT(#{businessCode},'%')
AND a.BUSINESS_NAME LIKE CONCAT(#{businessName},'%')
and a.REVIEW_TYPE = #{reviewType}
and a.REVIEW_STATUS = #{reviewStatus}
and a.company = #{company}
and a.AREA_CODE = #{areaCode}
and a.CREATOR = #{creator,jdbcType=INTEGER}
and a.flag = '1'


where BUSINESS_CODE = #{arg0}
and COMPANY = #{arg1}
and FLAG = '1'

mapper.update:
