    BUSINESS_CODE = #{businessCode}
and company = #{company}
and FLAG = '1'

update  MKMM_FEE_MAIN_REVIEW
set REVIEW_STATUS = '03',
    MODIFIER = #{modifier}
where
    BUSINESS_CODE = #{businessCode}
and COMPANY = #{company}
and FEE_TYPE = '01'
and FEE_SOURCE = '01'
and REVIEW_STATUS = '02'
and FLAG = '1'

